CREATE TABLE employee (
	id SERIAL PRIMARY KEY,
	first_name VARCHAR(50) NOT NULL,
	last_name VARCHAR(50) NOT NULL,
	birthday DATE,
	email VARCHAR(100)
);


-- filling null email values
UPDATE employee
SET email = 'not available'
WHERE email IS null;

SELECT * FROM employee
WHERE email = 'not available';

